Router=function(){this._routes=[]};Router.prototype.addRoute=function(e,t){var n=e.split("/");var r=e.split("/:")[0]||e;var i={ident:r,func:t,parts:n.length,parmsFrom:r.split("/").length};this._routes.push(i)};Router.prototype.find=function(e){var t=e.split("/:")[0]||e;var n=e.split("/");var r=n.length;for(var i=0,s=this._routes.length;i<s;i++){var o=this._routes[i];if(t.indexOf(o.ident)===0&&r===o.parts){o.func.apply(this,n.splice(o.parmsFrom));return}}}
